// class to be used in word game
export default class Letter {
    constructor(letter) {
        this.guessed = false;
        this.char = letter;
    }
    returnChar() {
        if (this.guessed) {
            return this.char;
        }
        else {
            return ("_");
        }
    }
    check(x) {
        if (this.char === x) {
            this.guessed = true;
            return true;
        }
        else {
            return false;
        }
    }
}
